import React, { Component, useState } from 'react'
import { Button, Table, Input, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Label, Col } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Tab } from 'bootstrap';

const corredor = (props) => {
  const {className} = props;

  const handlerChange = (event) =>{
    let target = event.target

  }

  return(
    <div>
      <Modal isOpen={props.mostrar} toggle={props.toggle} className={className} onEntering={() => {}}>
        <ModalHeader toggle={props.toggle}>AÑADIR CORREDOR</ModalHeader>
        <ModalBody>
          <FormGroup row>
            <Label sm={2}>Nombre: </Label>
            <Col sm={10}>
              <Input
                onChange={handlerChange}
                id='nombre'
                name='nombre'
                type='text'
              />
            </Col>
          </FormGroup>
          <FormGroup row>
            <Label sm={2}>Equipo: </Label>
            <Col sm={10}>
              <Input
                onChange={handlerChange}
                id='equipo'
                name='equipo'
                type='text'
              />
            </Col>
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color='primary'>AÑADIR CORREDOR</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

const Mostrar = (props) => {
  return(
    <>
      <Table>
        <thead>
          <tr>
            <th>#</th>
            <th>Nombre</th>
            <th>Equipo</th>
            <th>Posición</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th scope='row'>1</th>
            <td>Mark</td>
            <td>Otto</td>
            <td><Button>UP</Button></td>
          </tr>
        </tbody>
      </Table>
    </>
  );
} 
class App extends Component {
  constructor(props){
    super(props);

    this.state = {
      listaCorredores: [{nombre:"Eleuterio Casas", equipo:"Rambito Team", posicion:"1"},
                        {nombre:"Francis NGanou", equipo:"Rambito Team", posicion:"2"},
                        {nombre:"Emilio Anaya", equipo:"Istan Team", posicion:"3"},
      ],
      isOpen: false
    }
  }

  toggleModal(){
    this.setState({isOpen:!this.state.isOpen})
  }

  render(){
    return (
      <div className="App">
        <h1>CARRERAS DE ORIENTACIÖ</h1>
      </div>
    );
  }
}
export default App;
