      <Producto img={props.liPro[1].imagen} nombre={props.liPro[1].nombre} texto={props.liPro[1].texto} comprar={(nombre, id) => props.comprar(nombre, id)} id={props.liPro[1].id} />
      <Producto img={props.liPro[2].imagen} nombre={props.liPro[2].nombre} texto={props.liPro[2].texto} comprar={(nombre, id) => props.comprar(nombre, id)} id={props.liPro[2].id} />

